import React, { useState, useEffect } from 'react';

const ROWS = 6;
const COLS = 7;

export default function Connect4Board() {
  // Initialize board with 0s (empty) to match Python backend
  const [board, setBoard] = useState(Array(ROWS).fill(null).map(() => Array(COLS).fill(0)));
  const [playerTurn, setPlayerTurn] = useState(true); // true for human (Player 1), false for AI (Player 2)
  const [gameOver, setGameOver] = useState(false);
  const [winner, setWinner] = useState(null); // null, 0 (tie), 1 (Player 1), 2 (Player 2)
  const [message, setMessage] = useState("It's your turn!");

  // Function to reset the game state
  const resetGame = async () => {
    try {
      await fetch('http://localhost:5001/reset', { method: 'POST' });
      setBoard(Array(ROWS).fill(null).map(() => Array(COLS).fill(0)));
      setPlayerTurn(true);
      setGameOver(false);
      setWinner(null);
      setMessage("Game reset! It's your turn!");
    } catch (error) {
      console.error("Error resetting game:", error);
      // Reset frontend state even if backend reset fails
      setBoard(Array(ROWS).fill(null).map(() => Array(COLS).fill(0)));
      setPlayerTurn(true);
      setGameOver(false);
      setWinner(null);
      setMessage("Game reset! (Backend may need manual restart)");
    }
  };

  const handleClick = async (col) => {
    console.log("Column clicked:", col);
    console.log("Player turn:", playerTurn);
    console.log("Game over:", gameOver);

    if (!playerTurn || gameOver) {
      console.log("Click ignored - not player turn or game over");
      return;
    }

    setMessage("Making your move...");

    try {
      console.log("Sending move to backend:", col);
      const res = await fetch('http://localhost:5001/move', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ column: col })
      });

      console.log("Response status:", res.status);

      if (!res.ok) {
        throw new Error(`HTTP error! status: ${res.status}`);
      }

      const data = await res.json();
      console.log("Response data:", data);

      if (data.error) {
        setMessage(data.error);
        return;
      }

      setBoard(data.board);

      if (data.game_over) {
        setGameOver(true);
        setWinner(data.winner);
        if (data.winner === 1) setMessage("You Win! ðŸŽ‰");
        else if (data.winner === 2) setMessage("AI Wins! ðŸ¤–");
        else setMessage("It's a Tie! ðŸ¤");
      } else {
        setPlayerTurn(false); // Switch to AI's turn
        setMessage("AI is thinking...");
      }
    } catch (error) {
      console.error("Error making human move:", error);
      setMessage("âŒ Cannot connect to backend. Make sure Flask server is running on localhost:5001");
    }
  };

  useEffect(() => {
    // Only fetch AI move if it's AI's turn and game is not over
    if (!playerTurn && !gameOver) {
      const getAIMove = async () => {
        try {
          const res = await fetch('http://localhost:5001/ai-move');

          if (!res.ok) {
            throw new Error(`HTTP error! status: ${res.status}`);
          }

          const data = await res.json();

          if (data.error) {
            setMessage(data.error);
            return;
          }

          setBoard(data.board); // Update board with AI's move

          if (data.game_over) {
            setGameOver(true);
            setWinner(data.winner);
            if (data.winner === 1) setMessage("You Win! ðŸŽ‰");
            else if (data.winner === 2) setMessage("AI Wins! ðŸ¤–");
            else setMessage("It's a Tie! ðŸ¤");
          } else {
            setPlayerTurn(true); // Switch back to human's turn
            setMessage("It's your turn!");
          }
        } catch (error) {
          console.error("Error getting AI move:", error);
          setMessage("Error: Cannot connect to backend for AI move");
        }
      };
      const aiThinkingTimer = setTimeout(getAIMove, 500); // Give a small delay for AI to 'think'
      return () => clearTimeout(aiThinkingTimer); // Cleanup timeout if component unmounts
    }
  }, [playerTurn, gameOver]); // Depend on playerTurn and gameOver states

  // Function to determine cell color based on player number
  const getCellColor = (cellValue) => {
    if (cellValue === 1) return 'bg-red-500'; // Player 1
    if (cellValue === 2) return 'bg-yellow-400'; // Player 2
    return 'bg-white'; // Empty
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-blue-900 via-blue-800 to-purple-800 p-4 text-white">
      <h1 className="text-5xl font-bold mb-6 text-white drop-shadow-lg">Connect 4</h1>

      <div className="mb-6 text-2xl font-semibold text-center min-h-8">
        {message}
      </div>

      <div className="relative">
        {/* Column click areas */}
        <div className="absolute -top-16 left-0 right-0 h-16 flex z-10">
          {Array.from({ length: COLS }).map((_, colIndex) => (
            <div
              key={`click-${colIndex}`}
              onClick={() => handleClick(colIndex)}
              className="flex-1 h-full cursor-pointer hover:bg-white hover:bg-opacity-20 rounded-t-lg transition-all duration-200 flex items-center justify-center"
              style={{ minHeight: '64px' }}
            >
              <div className="w-3 h-3 bg-red-500 rounded-full opacity-0 hover:opacity-100 transition-opacity duration-200" />
            </div>
          ))}
        </div>

        {/* Game board */}
        <div className="grid grid-cols-7 gap-2 p-4 bg-blue-600 rounded-2xl shadow-2xl border-4 border-blue-500">
          {board.map((row, rowIndex) => (
            row.map((cell, colIndex) => (
              <div
                key={`${rowIndex}-${colIndex}`}
                className="w-16 h-16 bg-blue-800 rounded-full flex items-center justify-center shadow-inner"
              >
                <div 
                  className={`w-12 h-12 rounded-full ${getCellColor(cell)} shadow-lg transition-all duration-300 ease-out border-2 border-opacity-20 border-white`}
                />
              </div>
            ))
          ))}
        </div>
      </div>

      {gameOver && (
        <button
          onClick={resetGame}
          className="mt-8 px-8 py-4 bg-green-500 hover:bg-green-600 text-white font-bold text-xl rounded-xl shadow-lg transition-all duration-200 transform hover:scale-105"
        >
          Play Again
        </button>
      )}

      <div className="mt-6 text-sm text-blue-200 text-center">
        <p>Click on a column to drop your piece (red)</p>
        <p>Connect 4 pieces horizontally, vertically, or diagonally to win!</p>
      </div>
    </div>
  );
}
